const html_users=document.querySelector(".users-list");fetch("https://62f3b187a84d8c968129c2fb.mockapi.io/onios/users").then(e=>e.json()).then(e=>{e.forEach(e=>{html_users.insertAdjacentHTML("afterbegin",`<li>
          <p>ID: ${e.id}</p>
          <p>CreatedAt: ${e.createdAt}</p>
          <p>Name: ${e.name}</p>
          <img src="${e.avatar}"/>
        </li>`)})});